import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';
import Cart from './Cart';

const TopHead = ({ isLogin, credentials }) => {
  const [ten, setTen] = useState();

  useEffect(() => {
    if (credentials) setTen(credentials.ten);
  }, [isLogin, credentials]);

  return (
    <Container>
      <Logo>
        <Link to="/">
          <img src="../static/logo.png" alt="logo" width="50px" height="50px" />
        </Link>
      </Logo>
      <Form>
        <DivInput>
          <Input placeholder="Bạn tìm gì.." />
        </DivInput>
      </Form>
      <DonHangDiv>
        {isLogin ? (
          <LinkStyled to="/order">Đơn Hàng Của {ten}</LinkStyled>
        ) : (
          <LinkStyled to="/login">Đơn Hàng Của Bạn</LinkStyled>
        )}
      </DonHangDiv>
      <Cart />
    </Container>
  );
};

// Redux

const mapStateToProps = (state) => {
  return state.user;
};

// styled

const DivInput = styled.div`
  position: absolute;
  margin-left: 30px;
  margin-top: 5px;
`;

const Container = styled.div`
  margin: 0 0 0 250px;
  height: 50px;
`;

const Logo = styled.div`
  position: absolute;
`;

const Form = styled.form`
  margin-top: 5px;
  margin-left: 30px;
`;

const Input = styled.input`
  width: 600px;
  height: 40px;
  border-radius: 5px;
  font-size: 22px;
  border: 0px solid;
  padding-left: 15px;
  padding-bottom: 5px;
`;

const DonHangDiv = styled.div`
  color: #fff;
  height: 50px;
  position: absolute;
`;

const LinkStyled = styled(Link)`
  :active,
  :visited,
  :hover,
  :link {
    color: #fff;
    text-decoration: none;
  }
  display: block;
  text-align: center;
  width: 104px;
  font-size: 14px;
  padding: 4px 15px 4px 15px;
`;

export default connect(mapStateToProps)(TopHead);
