import React, { useState } from 'react';
import styled from 'styled-components';
import ChildItemCatalog from './ChildItemCatalog';

const Title = styled.div`
  text-transform: uppercase;
`;

const Button = styled.button``;

const sanphanTest = [
  'sanpham',
  'sanpham',
  'sanpham',
  'sanpham',
  'sanpham',
  'sanpham',
];

const listItems = sanphanTest.map((item) => (
  <ChildItemCatalog key={Math.random()} items={item} />
));

const ItemCatalog = ({ catalogs }) => {
  const [showChild, setShowChild] = useState(false);
  const onClick = () => setShowChild(!showChild);
  return (
    <div>
      <Title>
        <Button onClick={onClick}>{catalogs}</Button>
      </Title>

      {showChild ? listItems : null}
    </div>
  );
};

export default ItemCatalog;
